    public static int Erfen(int[] S,int num){
        if(S.length==0) return -1;
        int min=0;int max=S.length-1;
        int dex=-1;
        while (min!=max){
            int mid=(min+max)/2;
            if(S[mid]>num) {
                max=mid-1;
            } else if (S[mid]<num) {
                min=mid+1;

            }
            else{
                dex=mid;
                break;
        }
        }
        return dex;
    }
    public static int Erfenpro(int[] S,int num){
        if(S.length==0) return -1;
        int min=0;int max=S.length-1;
        int dex=-1;
        while (min<max){
            int mid=min+((num-S[min])/(S[max]-S[min]))*(max-min);
            if(S[mid]>num) {
                max=mid-1;
            } else if (S[mid]<num) {
                min=mid+1;

            }
            else{
                dex=mid;
                break;
            }
        }
        return dex;
    }
